INSERT INTO `component` VALUES ('1', 'v-img', '[{name:1}]', '{\r\n	template:\'<div @click.stop=\"imgClick($event)\" style=\"width:100%;height:100%;\"><img style=\"width:100;height:100%;\" :src=\"data.VUELOGO.img\" /></div>\',\r\n	name:\"v-img\" ,\r\n	data:function(){return{\r\n			type:\"VUELOGO\" , //组件类型\r\n			id:\"1\" , //组件id\r\n		}\r\n	} ,\r\n	props:{\r\n		data:String ,\r\n		now:Object\r\n	} ,\r\n	methods:{imgClick:function(e){\r\n			this.now[\"type\"] = this.type ;\r\n			var  self = this.data.VUELOGO ;\r\n			e.currentTarget.parentElement.style.border=\"1px solid blue\";\r\n			var file = document.createElement(\"input\");\r\n			file.type = \"file\" ;\r\n			file.onchange = function(e){\r\n				var f = \"\" ;\r\n				if(e.path == undefined || typeof(e.path)){\r\n					f = e.target.files[0] ;\r\n				}else{\r\n					f = e.path[0].files[0] ;\r\n				}\r\n				if(undefined == f) {\r\n					return;\r\n				}\r\n				r = new FileReader();\r\n				r.readAsDataURL(f);\r\n				var base64 = \"\";\r\n				r.onload = function(e) {\r\n					base64 = e.target.result;\r\n					self.img = base64 ;\r\n				}\r\n			}\r\n			file.click() ;\r\n		}\r\n	}\r\n}', './img/image.png', '30', '30', '图片');
