INSERT INTO `component` VALUES ('8', 'v-bar_icon', '[{name:3},{name:4}]', '{\r\n	template:\'<div @click.stop=\"ClockClick($event)\" style=\"width:100%;height:100%;display:flex;justify-content:space-between;\"><div v-for=\"(item,index) in data.MAIN.data\" @click.stop=\"setContent($event , index)\" style=\"cursor:pointer;height:100%;width:50px;\" :style=\"{background:(index == data.MAIN.item) ? \\\'white\\\' : \\\'none\\\'}\" class=\"vue-content-item\" ><img :src=\"item.img\" style=\"width:100%;height:100%;\" /></div></div>\',\r\n	name:\"v-bar_icon\" ,\r\n	data:function(){return{\r\n			type:\"MAIN\" , //组件类型\r\n			id:\"8\" , //组件id\r\n			msg:\"导航栏组件，所有选项都是图标\",\r\n			typeid:\"bar_icon\" \r\n		}\r\n	} ,\r\n	props:{\r\n		data:String ,\r\n		now:Object ,\r\n		fields:Object\r\n	} ,\r\n	created:function(){\r\n		if(this.data.MAIN.bar && this.data.MAIN.data.length > 1){\r\n			return ;\r\n		}\r\n		this.data.MAIN.data.splice(0,this.data.MAIN.data.length);\r\n		var item1 = {id:\"0\" , name:\"主页\" , img:\"./img/zhuye.png\" , selectedImg:\"\" , selectColor:\"white\" , data:{id:7 , typeid:\"CONTENT_2\" , info:[{\"apk\":\"\" , apkName:\"\" , type:\"img\" , imgs:[\"./img/zhuye_content_1.jpg\" , \"./img/_20190524095107.png\"] },{\"apk\":\"\" , apkName:\"\" , type:\"img\" , imgs:[\"./img/ic_home.png\"] }]}} ;\r\n		var item2 = {id:\"1\" , name:\"电视直播\" , img:\"./img/zhibo.png\" , selectedImg:\"\" , selectColor:\"white\" , data:{id:5 , typeid:\"CONTENT_3\" , info:[{\"apk\":\"\" , apkName:\"\" , type:\"img\" , imgs:[\"./img/iptv_adv_a.png\"] },{\"apk\":\"\" , apkName:\"\" , type:\"img\" , imgs:[\"./img/iptv_dszb.png\"]},{\"apk\":\"\" , apkName:\"\" , type:\"img\" , imgs:[\"./img/movies_adv_a.png\"]}]}} ;\r\n		var item3 = {id:\"2\" , name:\"高清影视\" , img:\"./img/ys.png\" , selectedImg:\"\" , selectColor:\"white\" , data:{id:5 , typeid:\"CONTENT_3\" , info:[{\"apk\":\"\" , apkName:\"\" , type:\"img\" , imgs:[\"./img/iptv_adv_b.png\"] },{\"apk\":\"\" , apkName:\"\" , type:\"img\" , imgs:[\"./img/movies_gqys.png\"]},{\"apk\":\"\" , apkName:\"\" , type:\"img\" , imgs:[\"./img/movies_adv_b.png\"] }]}} ;\r\n		var item4 = {id:\"3\" , name:\"我的酒店\" , img:\"./img/jd.png\" , selectedImg:\"\" , selectColor:\"white\" , data:{id:6 , typeid:\"CONTENT_4\" , info:[{\"apk\":\"\" , apkName:\"\" , type:\"img\" , imgs:[\"./img/hote_jdjs_ic.png\"] },{\"apk\":\"\" , apkName:\"\" , type:\"img\" , imgs:[\"./img/hote_ctjs_ic.png\"] },{\"apk\":\"\" , apkName:\"\" , type:\"img\" , imgs:[\"./img/hote_hyyh_ic.png\"]},{\"apk\":\"\" , apkName:\"\" , type:\"img\" , imgs:[\"./img/iptv_adv_b.png\"]}]}} ;\r\n		var item5 = {id:\"4\" , name:\"智慧旅游\" , img:\"./img/ly.png\" , selectedImg:\"\" , selectColor:\"white\" , data:{id:6 , typeid:\"CONTENT_4\" , info:[{\"apk\":\"\" , apkName:\"\" , type:\"img\" , imgs:[\"./img/travel_lyjd_ic.png\"] },{\"apk\":\"\" , apkName:\"\" , type:\"img\" , imgs:[\"./img/travel_jxms_ic.png\"] },{\"apk\":\"\" , apkName:\"\" , type:\"img\" , imgs:[\"./img/travel_jxtc_ic.png\"] },{\"apk\":\"\" , apkName:\"\" , type:\"img\" , imgs:[\"./img/travel_zbcx_ic.png\"]}]}} ;\r\n		var item6 = {id:\"5\" , name:\"互动娱乐\" , img:\"./img/yl.png\" , selectedImg:\"\" , selectColor:\"white\" , data:{id:6 , typeid:\"CONTENT_4\" , info:[{\"apk\":\"\" , apkName:\"\" , type:\"img\" , imgs:[\"./img/_20190531153218.png\"]},{\"apk\":\"\" , apkName:\"\" , type:\"img\" , imgs:[\"./img/_20190531153230.png\"]},{\"apk\":\"\" , apkName:\"\" , type:\"img\" , imgs:[\"./img/_20190531153239.png\"]},{\"apk\":\"\" , apkName:\"\" , type:\"img\" , imgs:[\"./img/_20190531153247.png\"]}]}} ;\r\n		this.data.MAIN.data.push(item1) ;\r\n		this.data.MAIN.data.push(item2) ; \r\n		this.data.MAIN.data.push(item3) ;\r\n		this.data.MAIN.data.push(item4) ;\r\n		this.data.MAIN.data.push(item5) ;\r\n		this.data.MAIN.data.push(item6) ;\r\n		this.data.MAIN.item = 0 ;\r\n		this.data.MAIN.type = this.typeid ;\r\n	} ,\r\n	methods:{ClockClick:function(e){\r\n			this.now[\"type\"] = this.type ;\r\n			e.currentTarget.parentElement.style.border=\"1px solid blue\";\r\n		} ,\r\n		setContent:function(e , index){\r\n			this.now[\"type\"] = this.type ;\r\n			this.now[\"bar_item\"] = index ;\r\n			this.data.MAIN.item = index ;\r\n			e.currentTarget.parentElement.parentElement.style.border=\"1px solid blue\"; \r\n		} \r\n	} \r\n}', './img/image.png', '30', '30', '导航栏组件');

