INSERT INTO `component` VALUES ('1', 'v-img', '[{name:1}]', '{\r\n	template:\'<div @click.stop=\"imgClick($event)\" style=\"width:100%;height:100%;\"><img style=\"width:100;height:100%;\" :src=\"data.img\" /></div>\',\r\n	name:\"v-img\" ,\r\n	data:function(){return{\r\n			type:\"VUELOGO\" , //组件类型\r\n			id:\"1\" , //组件id\r\n		}\r\n	} ,\r\n	props:{\r\n		data:String ,\r\n		now:Object\r\n	} ,\r\n	methods:{imgClick:function(e){\r\n			this.now[\"type\"] = this.type ;\r\n			var  self = this.data ;\r\n			e.currentTarget.parentElement.style.border=\"1px solid blue\";\r\n			var file = document.createElement(\"input\");\r\n			file.type = \"file\" ;\r\n			file.onchange = function(e){\r\n				var f = \"\" ;\r\n				if(e.path == undefined || typeof(e.path)){\r\n					f = e.target.files[0] ;\r\n				}else{\r\n					f = e.path[0].files[0] ;\r\n				}\r\n				if(undefined == f) {\r\n					return;\r\n				}\r\n				r = new FileReader();\r\n				r.readAsDataURL(f);\r\n				var base64 = \"\";\r\n				r.onload = function(e) {\r\n					base64 = e.target.result;\r\n					self.img = base64 ;\r\n				}\r\n			}\r\n			file.click() ;\r\n		}\r\n	}\r\n}', './img/image.png', '30', '30', '图片');
INSERT INTO `component` VALUES ('2', 'v-pmd', '[{name:6},{name:7}]', '{\r\n	template:\'<div @click.stop=\"pmdClick($event)\" style=\"width:100%;height:100%;background:url(./img/pmd.png);\"><marquee :style=\"{fontSize:data.size+\\\'px\\\',color:data.color}\" v-if=\"flag\">{{data.title}}</marquee><input v-if=\"!flag\" v-model=\"data.title\" type=\"text\" style=\"width:100%;height:100%;\" /></div>\',\r\n	name:\"v-img\" ,\r\n	data:function(){return{\r\n			type:\"VUEPMD\" , //组件类型\r\n			id:\"2\" , //组件id\r\n			flag:true \r\n		}\r\n	} ,\r\n	props:{\r\n		data:String ,\r\n		now:Object ,\r\n		fields:Object\r\n	} ,\r\n	created:function(){\r\n		this.data.title = \"点击为跑马灯添加内容\" ;\r\n	} ,\r\n	methods:{pmdClick:function(e){\r\n			this.now[\"type\"] = this.type ;\r\n			e.currentTarget.parentElement.style.border=\"1px solid blue\";\r\n			//console.log(this.flag)\r\n			this.flag = false ;\r\n			/*for(var i in this.fields){\r\n				if(i == 0){continue;}\r\n				if(i == 1){\r\n					this.fields[i] = true ;\r\n				}else{\r\n					this.fields[i] = false ;\r\n				}\r\n			}*/\r\n		}\r\n	},\r\n	watch:{\r\n		\"now.type\":function(n , o){\r\n			if(o == this.type){\r\n				this.flag = true ;\r\n			}\r\n		}\r\n	}\r\n}', './img/image.png', '30', '30', '跑马灯');
