INSERT INTO `component` VALUES ('4', 'v-content5', '[{name:5}]', '{\r\n	template:\'<div @click.stop=\"ClockClick($event)\" style=\"width:100%;height:100%;display:flex;justify-content:space-between;\"><div v-for=\"(item,index) in (data.bar ? data.data[data.item].data.info : info.data.info)\" @click.stop=\"setContent($event , index)\" style=\"cursor:pointer;width:16%;height:100%;\" class=\"vue-content-item\" ><div :id=\"\\\'slider\\\'+index\" style=\"width:100%;height:100%;\" class=\"layui-carousel\"><div carousel-item><div v-for=\"m in item.imgs\"><img :src=\"m\" style=\"width:100%;height:100%;\" /></div></div></div></div></div>\',\r\n	name:\"v-content\" ,\r\n	data:function(){return{\r\n			type:\"MAIN\" , //组件类型\r\n			id:\"4\" , //组件id\r\n			msg:\"内容组件,平分5份\",\r\n			typeid:\"CONTENT_5\" ,\r\n			info:{id:\"0\" , name:\"默认\" , img:\"\" , selectedImg:\"\" , selectedColor:\"\" , selectedBorder:\"\" , data:{id:4 , typeid:\"CONTENT_5\" , info:[{\"apk\":\"\" , apkName:\"\" , type:\"img\" , imgs:[\"./img/content.png\" , \"./img/image.png\"]},{\"apk\":\"\" , apkName:\"\" , type:\"img\" , imgs:[\"./img/content.png\"]},{\"apk\":\"\" , apkName:\"\" , type:\"img\"  , imgs:[\"./img/content.png\"]},{\"apk\":\"\" , apkName:\"\" , type:\"img\"  , imgs:[\"./img/content.png\"]},{\"apk\":\"\" , apkName:\"\" , type:\"img\" , imgs:[\"./img/content.png\"]}]}} , //此处img与image的区别是img为单张图片，image为多张图片\r\n		}\r\n	} ,\r\n	props:{\r\n		data:String ,\r\n		now:Object ,\r\n		fields:Object\r\n	} ,\r\n	created:function(){\r\n		if(!this.data.bar){\r\n			this.data.data.push(this.info) ;\r\n		//	this.info = this.data.data[this.data.item] ;\r\n		}else if(this.data.data[this.data.item].data.length == 0){\r\n			this.data.data[this.data.item].data = this.info.data ;\r\n		}\r\n		this.$nextTick(function(){\r\n			\r\n			layui.use(\"carousel\", function(){\r\n			  var carousel = layui.carousel;\r\n\r\n			  for(var i=0 ; i<6 ; i++){\r\n			  		//建造实例\r\n				  carousel.render({\r\n				    elem: \"#slider\"+i\r\n			